import React from "react";
import { useDrag } from "react-dnd";

const SideBarItem = ({ data }) => {
  const [{ opacity }, drag] = useDrag({
    item: data,
    collect: monitor => ({
      opacity: monitor.isDragging() ? 0.4 : 1
    })
  });

  var svg_element = 'M0 30 L30 30 L30 -100 L30 150'

  return (
    <div className="sideBarItem" ref={drag} style={{ opacity }}>
       <svg id="open_contact" height="60" width="80">
        <g fill="none" stroke="black">
          <path stroke-width="3" d={svg_element} fill="none" />
          <path stroke-width="3" d="M50 30 L50 -100 L50 100 L50 30 L80 30" fill="none" />
        </g>
      </svg>

      <img src={'/images/'+data.component.content + '.jpeg'} />
    </div>
  );
};
export default SideBarItem;
